
if object_id('PolicyTest.TestParametersCasedCorrectly') is not null
drop procedure PolicyTest.TestParametersCasedCorrectly;
go

create procedure PolicyTest.TestParametersCasedCorrectly  
as  
/*******************************************************************************

PolicyTest.TestForUnusedParameters

Ensures that all stored procedure parameters going forward are used

*******************************************************************************/

set transaction isolation level read committed;
set nocount on;

if object_id('PolicyTest.expected') is not null drop table PolicyTest.expected;  
if object_id('PolicyTest.actual') is not null drop table PolicyTest.actual; 

-- Create a list of known incorrectly cased parameters to stop hundreds of failures
-- being raised immediately.  THIS LIST MUST NOT BE ADDED TO, but you are welcome
-- to remove from it if you are cleaning up old code!
if object_id('tempdb..#Exceptions') is not null drop table #Exceptions; 

create table #Exceptions
(
	SchemaName sysname,
	ObjectName sysname,
	ParameterName sysname,
	
	primary key (SchemaName, ObjectName, ParameterName)
);
  

	-- EMISSpokeMonitoring
	('dbo', 'GenerateNewErrorIdentifier', '@errorId'),
	('dbo', 'UpdateErrorIdentifier', '@errorId'),
	('dbo', 'UpdateErrorIdentifier', '@clientReferenceId'),
	('dbo', 'UpdateErrorIdentifier', '@hashValue'),
	('dbo', 'UpdateErrorIdentifier', '@isUserFacing')

--Generate list of exceptions. WI 103224 has been created to replace the above had coded list to be replaced using extended properties.
	SchemaName,
	ObjectName,
	ParameterName)
select	
	s.name as SchemaName,
	o.name as ObjectName,
	p.name as ParameterName
from sys.extended_properties ep
inner join sys.parameters p
	on p.object_id = ep.major_id
	and p.parameter_id = ep.minor_id
inner join sys.objects o
	on o.object_id = p.object_id
inner join sys.schemas s
	on s.schema_id = o.schema_id
left join #Exceptions e
	on e.SchemaName = s.name
	and e.ObjectName = o.name
	and e.ParameterName = p.name
where ep.name = 'UnitTestException_TestParametersCasedCorrectly'
and e.ObjectName is null

create table PolicyTest.expected  
(  
	SchemaName sysname,  
	ObjectName sysname,  
	ParameterName sysname  
);

select
	ss.name as SchemaName,
	o.name as ObjectName,
	ap.name as ParameterName
into PolicyTest.actual
from PolicyTest.ProductObject o
join sys.schemas ss on ss.schema_id = o.schema_id
join sys.all_parameters ap on ap.object_id = o.object_id
left join #Exceptions e on e.SchemaName = ss.name and e.ObjectName = o.name and e.ParameterName = ap.name
join sys.sql_modules m on m.object_id = o.object_id
where ascii(substring(ap.name, 2, 1)) between ascii('a') and ascii('z')
and o.name not in ('sp_alterdiagram','sp_creatediagram','sp_dropdiagram','sp_helpdiagramdefinition','sp_helpdiagrams','sp_renamediagram','sp_upgraddiagrams') --system diagraming procedures.
and e.ObjectName is null
order by ss.name, o.name

exec tSQLt.AssertEqualsTable 'PolicyTest.expected', 'PolicyTest.actual', 'One or more stored procedures has parameters cased incorrectly!';